{
    scanf("%d%d",&n,&m);
    for(int i=1;i<=n;++i)
    {
        int a,b,c;
        scanf("%d%d%d",&b,&a,&c);
        for(int j=0;j<a;++j)
        {
            T=0;
            R=-1;
            for(int k=j;k<=m;k+=a)
            {
                while(T<=R&&k-p[T]>a*c) ++T;
                while(T<=R&&q[R]+(k-p[R])/a*b<=f[k]) --R;
                p[++R]=k;
                q[R]=f[k];
                f[k]=q[T]+(k-p[T])/a*b;
            }
        }
