    s1=1;
    s2=n*2+2;
    for(int i=1;i<=n;++i)
    {
        road(1,i+1,1,0);
        road(i+1,1,0,0);
    }
    for(int i=1;i<=n;++i)
    {
        road(i+n+1,n*2+2,1,0);
        road(n*2+2,i+n+1,0,0);
    }
    for(int i=1;i<=n;++i)
    {
        for(int j=1;j<=n;++j)
        {
            int w;
            scanf("%d",&w);
            road(i+1,j+n+1,1,w);
            road(j+n+1,i+1,0,-w);
        }
